package com.example.base64test1;

import androidx.appcompat.app.AppCompatActivity;
import android.graphics.Bitmap;
import android.graphics.BitmapFactory;
import android.os.Bundle;
import android.util.Base64;
import android.widget.ImageView;
import java.io.ByteArrayOutputStream;

public class MainActivity extends AppCompatActivity {

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        ImageView image1 = findViewById(R.id.image1);
        ImageView image2 = findViewById(R.id.image2);
        ImageView image3 = findViewById(R.id.image3);
        ImageView image4 = findViewById(R.id.image4);

        String base64_1 = "";
        String base64_2 = "";
        String base64_3 = "";
        String base64_4 = "";


        //------------------ encode image to base64 string -------------------------//
        ByteArrayOutputStream baos = new ByteArrayOutputStream();
//        Bitmap bitmap = BitmapFactory.decodeResource(getResources(), R.drawable.aa);
//        bitmap.compress(Bitmap.CompressFormat.JPEG, 100, baos);
        byte[] imageBytes = baos.toByteArray();
//        String imageString = Base64.encodeToString(imageBytes, Base64.DEFAULT);


        //------------------ decode base64 string to image -------------------------//

        // image1
        imageBytes = Base64.decode(base64_1, Base64.DEFAULT);
        Bitmap decodedImage1 = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes.length);
        image1.setImageBitmap(decodedImage1);

        // image2
        imageBytes = Base64.decode(base64_2, Base64.DEFAULT);
        Bitmap decodedImage2 = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes.length);
        image2.setImageBitmap(decodedImage2);

        // image3
        imageBytes = Base64.decode(base64_3, Base64.DEFAULT);
        Bitmap decodedImage3 = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes.length);
        image3.setImageBitmap(decodedImage3);

        // image4
        imageBytes = Base64.decode(base64_4, Base64.DEFAULT);
        Bitmap decodedImage4 = BitmapFactory.decodeByteArray(imageBytes, 0, imageBytes.length);
        image4.setImageBitmap(decodedImage4);
    }
}
